import React from 'react'
import { useState, useEffect } from 'react';
import axios from "axios"

function Part2() {
    const [planets, setPlanets] = useState([])

    const getPlanets = async() =>{
        
        const response = await axios.get(`https://swapi.py4e.com/api/planets`)

        let allPlanets = response.data.results

        filterWantedPlanets(allPlanets)
    }

    useEffect(() =>{
        
        getPlanets()
        
    }, [])


    function filterWantedPlanets(allPlanets){
        let filteredPlanets = []
        filteredPlanets = allPlanets.filter(planet => ((planet.population >= 200000 && planet.population <= 45000000000) && (planet.rotation_period < 27)))

        setPlanets(filteredPlanets)
        console.log(planets)
        
    }


    const Chart = ({ children, height, width}) =>{
        <svg viewBox={`0 0 ${width} ${height}`}
        height ={height}
        width = {width}
        >
                {children}

        </svg>
    }

    const Bar = ({x, y, height, width}) =>{
        <rect x={x} y={y} height={height} width={width}/>
    }

    const greatestValue = (values) =>{
        values.reduce( (acc, cur) => (cur > acc ? cur : acc), -Infinity)
    }

    const BarChart = (planets) =>{
        const barWidth =20
        const barMaergin = 5
        // const width = planets.length *(barWidth + barMaergin)
        const width = (barWidth + barMaergin)
        const height = greatestValue(planets.map(planet => planet.population))
        
        return (
            <Chart
            height={height}
            width={width}
            >
                {planets.map((datum, index) =>{
                    <Bar
                    key={datum.name}
                    x = {index *(barWidth + barMaergin)}
                    y = {0} 
                    width={barWidth}
                    height={datum.population}
                    />
                })}
            </Chart>
        )
    }
    
    return (
        <div>
            this is part 2

            <BarChart data={planets}/>

            {planets.map(planet => {
               return <div>{planet.name} {planet.population}
               </div> 
               
            })}
        </div>
    )
}

export default Part2
