import React from 'react'
import { useState, useEffect } from 'react';
import axios from "axios"

const baseEndpoint = `https://swapi.py4e.com/api`;
//const usersEndpoint = `${baseEndpoint}/people`;
//const plenetEndpoint = `${baseEndpoint}/planets`;

function Part1() {

    const [vehicles, setVehicles] = useState([])
    const [pilots, setPilots] = useState([])

    const [vehicle, setVehicle] =useState(null)
    const [planet, setPlanet] = useState(null)
    const [population, setPopulation] = useState(null)
    const [pilot, setPilot] = useState(null)

    const getVehicles = async() =>{
        
        const response = await axios.get(`${baseEndpoint}/vehicles`)
        console.log(response.data.results)

        setVehicles(response.data.results)
    }

    useEffect(() =>{
        
        getVehicles()
        
    }, [])


    async function getPlanet(pilotsDetailed){

        let planetsDetails = []

        for(let i = 0; i < pilotsDetailed.length; i++){

        }
        
        const response = await axios.get(`${pilotPlanet}`)
        console.log(response.data)
        setPlanet(response.data.name)
        setPopulation(response.data.population)
        
    }


    async function getPilotsDetails (allPilots){
        let pilotsDetailed = []
        for(let i = 0; i < allPilots.length; i++){
            //console.log(allPilots[i])

            //pilotsDetailed.push(allPilots[i])
            const response = await axios.get(`${allPilots[i]}`)
            //console.log(response.data)
            pilotsDetailed.push(response.data)
            // pilotsDetailed.push(response)
        }
        console.log(pilotsDetailed)
        
        getPlanet(pilotsDetailed)

        
        // for(let i = 0; i < pilotsDetailed)

        // const response = await axios.get(`${allPilots[1][0]}`)
        // console.log(response.data.homeworld)
        // console.log(response.data.name)
        // setPilot(response.data.name)

        // let pilotPlanet = response.data.homeworld
        // getPlanet(pilotPlanet)
        
        // const response2 = await axios.get(`${response.data.homeworld}`)
        // console.log(response2.data)

        //setPlanet(response2.data)
        //console.log(planet.name)

        // const response = await axios.get(`${allPilots[1]}`)
        // console.log(response.data.homeworld)
        
        // const response2 = await axios.get(`${response.data.homeworld}`)
        // console.log(response2.data.population)
    }

    // const getPilotsDetails = async(allPilots) =>{
    //     const response = await axios.get(pilot)
    //     allPilots.forEach(pilot =>{
    //     })
    // }


    //get the pilots
    function getPilots(vehiclesWithPilots){
        //console.log(vehiclesWithPilots)
        let allPilots = []

        // vehiclesWithPilots.forEach(vehicle =>{
        //     allPilots.push(vehicle.pilots)
        // })

        for(let i = 0; i < vehiclesWithPilots.length; i++){
            if(vehiclesWithPilots[i].pilots.length > 1){
                for(let j = 0; j < vehiclesWithPilots[i].pilots.length; j++){
                    allPilots.push(vehiclesWithPilots[i].pilots[j])
                }
            }
            else{
                allPilots.push(vehiclesWithPilots[i].pilots);
            }
            
            //allPilots.push(vehiclesWithPilots[i].population);
        }

        //console.log(allPilots)
        getPilotsDetails(allPilots)

    }

    //find which vehicles are assigned with pilots
    function findPilots(vehicles){
        let vehiclesWithPilots = []
        vehiclesWithPilots = vehicles.filter(vehicle => vehicle.pilots.length >= 1)
        getPilots(vehiclesWithPilots)
        
    }
    findPilots(vehicles)
    



    return (
        <div>
            Part 1:
            
            <table>
                <tr>
                    <td>Vehicle name with the largest sum</td>
                    <td>Vehicle name with the largest sum</td>
                </tr>
                <tr>
                    <td>Related home planets and their respective population</td>
                    <td>{planet}, {population}</td>
                </tr>
                <tr>
                    <td>Related pilot names</td>
                    <td>{pilot}</td>
                </tr>
            </table>
            

            
        </div>
    )
}

export default Part1
